import axios from 'axios';

const URL = 'http://localhost:3000';

export const getBooks = async () => {
  try {
    return await axios.get(`${URL}/books`);
  } catch (error) {
    console.error('Error while calling get books API', error);
    throw error; // Rethrow error for handling in components
  }
};

export const getBook = async (id) => {
  try {
    return await axios.get(`${URL}/books/editBook/${id}`);
  } catch (error) {
    console.error('Error while calling get book API', error);
    throw error; // Rethrow error for handling in components
  }
};

export const editBook = async (bookDetails) => {
  try {
    return await axios.put(`${URL}/books/editBook/${bookDetails.id}`, bookDetails);
  } catch (error) {
    console.error('Error while calling edit book API', error);
    throw error; // Rethrow error for handling in components
  }
};

// Correct addBook function with consistent property names
export const addBook = async (title, author, no_of_pages, genre, published_at) => {
  try {
    const bookDetails = {
      title: title,
      author: author,
      no_of_pages: no_of_pages, // Consistent property name
      genre: genre,
      published_at: published_at, // Consistent property name
    };

    console.log(bookDetails,'book dertails')

    return await axios.post(`${URL}/books/addBook`, bookDetails);
  } catch (error) {
    console.error('Error while calling add book API', error);
    throw error; // Rethrow error for handling in components
  }
};

export const deleteBook = async (id) => {
  try {
    return await axios.delete(`${URL}/books/${id}`);
  } catch (error) {
    console.error('Error while calling delete book API', error);
    throw error; // Rethrow error for handling in components
  }
};
