import React, { useState } from 'react';
import { addBook } from '../../service/api';
import './addBook.css';
import 'animate.css';

const AddBook = () => {
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [bookPages, setBookPages] = useState(0);
  const [publishDate, setPublishDate] = useState('');
  const [genre, setGenre] = useState('');

  const updateTitle = (e) => {
    setTitle(e.target.value);
  };

  const updateAuthor = (e) => {
    setAuthor(e.target.value);
  };

  const updateBookPages = (e) => {
    setBookPages(e.target.value);
  };

  const updatePublishDate = (e) => {
    setPublishDate(e.target.value);
  };

  const updateGenre = (e) => {
    setGenre(e.target.value);
  };

  const addNewBook = async () => {
    const response = await addBook(title, author, bookPages, genrepublishDate);
    document.querySelector('.modal-text').textContent = response.data.message;
    document.querySelector('.modal').classList.toggle('hide-modal');
  };

  const closeModal = () => {
    document.querySelector('.modal').classList.toggle('hide-modal');
  };

  return (
    <section>
      <div className='form-container animate__animated animate__backInDown'>
        <div className='modal hide-modal animate__animated animate__tada'>
          <button className='close-btn' onClick={closeModal}>
            X
          </button>
          <p className='modal-text'></p>
          <a href='/books'>
            <button className='nav-back-btn'>Back to Collection</button>
          </a>
        </div>
        <div className='add-book-form'>
          <h1>Add a new book.</h1>
          <div className='input-container'>
            <label>Title: </label>
            <input
              type='text'
              name='title'
              id='title'
              placeholder='Title'
              value={title}
              onChange={updateTitle}
              required
            />
          </div>
          <div className='input-container'>
            <label>Author: </label>
            <input
              type='text'
              name='author'
              id='author'
              placeholder='Author'
              value={author}
              onChange={updateAuthor}
              required
            />
          </div>
          <div className='input-container'>
            <label># of Pages: </label>
            <input
              type='number'
              name='no_of_pages'
              id='no_of_pages'
              placeholder='Total Pages'
              value={bookPages}
              onChange={updateBookPages}
              required
            />

          <div className='input-container'>
            <label>Genre: </label>
            <input
              type='text'
              name='genre'
              id='genre'
              placeholder='Genre'
              value={genre}
              onChange={updateGenre}
              required
            />
          </div>

          </div>
          <div className='input-container'>
            <label>Date Published: </label>
            <input
              type='date'
              name='publish_date'
              id='publish_date'
              value={publishDate}
              onChange={updatePublishDate}
              required
            />
          </div>
          
          <button className='addBook-btn' onClick={addNewBook}>
            Add Book
          </button>
        </div>
      </div>
    </section>
  );
};

export default AddBook;
