import React, { useState } from 'react';
import { addBook } from '../../service/api';
import './addBook.css';
import 'animate.css';

const fsPromises = require('fs').promises;
const path = require('path');
const multer = require('multer');

const data = {
  books: require('../model/books.json'),
  setBooks: function (data) {
    this.books = data;
  },
};

const AddBook = () => {
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [bookPages, setBookPages] = useState('');
  const [publishDate, setPublishDate] = useState('');
  const [genre, setGenre] = useState('');
  const [pdfFile, setPdfFile] = useState(null);  // New state for PDF
  const [modalMessage, setModalMessage] = useState('');
  const [modalVisible, setModalVisible] = useState(false);

  console.log(genre, 'genre');

  const createNewBook = async (req, res) => {
    const { title, author, no_of_pages, published_at, genre } = req.body;
  
    if (!title || !author || !no_of_pages || !published_at || !genre) {
      console.log('Missing required fields:', {
        title,
        author,
        no_of_pages,
        published_at,
        genre
      });
      return res.status(400).json({ message: 'Please enter all required details!' });
    }
  
    const newBook = {
      id: data.books.length ? data.books[data.books.length - 1].id + 1 : 1,
      title,
      author,
      no_of_pages: parseInt(no_of_pages),
      published_at,
      genre,
      pdf: req.file ? req.file.path : null // Store PDF file path if provided
    };
  
    data.setBooks([...data.books, newBook]);
    await fsPromises.writeFile(path.join(__dirname, '..', 'model', 'books.json'), JSON.stringify(data.books));
    res.status(201).json({ message: 'Book added successfully!' });
  };
  

  const closeModal = () => {
    setModalVisible(false);
  };

  return (
    <section>
      <div className='form-container animate__animated animate__backInDown'>
        {modalVisible && (
          <div className='modal animate__animated animate__tada'>
            <button className='close-btn' onClick={closeModal}>
              X
            </button>
            <p className='modal-text'>{modalMessage}</p>
            <a href='/books'>
              <button className='nav-back-btn'>Back to Collection</button>
            </a>
          </div>
        )}
        <div className='add-book-form'>
          <h1>Add a new book.</h1>
          <div className='input-container'>
            <label>Title: </label>
            <input
              type='text'
              name='title'
              id='title'
              placeholder='Title'
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>
          <div className='input-container'>
            <label>Author: </label>
            <input
              type='text'
              name='author'
              id='author'
              placeholder='Author'
              value={author}
              onChange={(e) => setAuthor(e.target.value)}
              required
            />
          </div>
          <div className='input-container'>
            <label># of Pages: </label>
            <input
              type='number'
              name='no_of_pages'
              id='no_of_pages'
              placeholder='Total Pages'
              value={bookPages}
              onChange={(e) => setBookPages(e.target.value)}
              required
            />
          </div>
          
          <div className='input-container'>
            <label>Date Published: </label>
            <input
              type='date'
              name='publish_date'
              id='publish_date'
              value={publishDate}
              onChange={(e) => setPublishDate(e.target.value)}
              required
            />
          </div>

          <div className='input-container'>
            <label>Genre: </label>
            <input
              type='text'
              name='genre'
              id='genre'
              placeholder='Genre'
              value={genre}
              onChange={(e) => setGenre(e.target.value)}
              required
            />
          </div>

          {/* New input for PDF file */}
          <div className='input-container'>
            <label>Upload PDF: </label>
            <input
              type='file'
              name='pdf'
              id='pdf'
              accept='application/pdf'
              onChange={(e) => setPdfFile(e.target.files[0])}
            />
          </div>

          <button className='addBook-btn' onClick={addNewBook}>
            Add Book
          </button>
        </div>
      </div>
    </section>
  );
};

export default AddBook;
