import React, { useEffect, useState } from 'react';
import { getBooks, addBook, editBook, deleteBook } from '../../service/api';

const BooksManager = () => {
  const [books, setBooks] = useState([]);

  useEffect(() => {
    const fetchBooks = async () => {
      try {
        const response = await getBooks();
        setBooks(response.data);
      } catch (error) {
        console.error('Error fetching books:', error);
      }
    };

    fetchBooks();
  }, []);

  const handleAddBook = async (bookDetails) => {
    try {
      const formData = new FormData();
      Object.keys(bookDetails).forEach(key => formData.append(key, bookDetails[key]));
      await addBook(formData);
      // Refresh book list after adding a new book
      const response = await getBooks();
      setBooks(response.data);
    } catch (error) {
      console.error('Error adding book:', error);
    }
  };

  const handleEditBook = async (bookDetails) => {
    try {
      const formData = new FormData();
      Object.keys(bookDetails).forEach(key => formData.append(key, bookDetails[key]));
      await editBook(formData);
      // Refresh book list after editing a book
      const response = await getBooks();
      setBooks(response.data);
    } catch (error) {
      console.error('Error editing book:', error);
    }
  };

  const handleDeleteBook = async (id) => {
    try {
      await deleteBook(id);
      // Refresh book list after deleting a book
      const response = await getBooks();
      setBooks(response.data);
    } catch (error) {
      console.error('Error deleting book:', error);
    }
  };

  return (
    <div>
      <h1>Books Manager</h1>
      {/* Render books list and provide functions to add, edit, delete books */}
    </div>
  );
};

export default BooksManager;
