import React, { useState, useEffect } from 'react';
import { getBooks, deleteBook } from '../../service/api';
import './books.css';
import 'animate.css';

const Books = () => {
  const [books, setBooks] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [filteredBooks, setFilteredBooks] = useState([]);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    getAllBooks();
  }, []);

  // Fetch all books from the API
  const getAllBooks = async () => {
    setLoading(true);
    try {
      const response = await getBooks();
      const sortedBooks = sortBooksByPages(response.data);
      setBooks(sortedBooks);
      setFilteredBooks(sortedBooks); // Initialize filteredBooks with sorted books
    } catch (error) {
      console.error('Error fetching books:', error);
    } finally {
      setLoading(false);
    }
  };

  // Sort books by the number of pages
  const sortBooksByPages = (booksArray) => {
    return booksArray.sort((a, b) => a.no_of_pages - b.no_of_pages);
  };

  // Delete a book from the collection
  const deleteBookFromCollection = async (id) => {
    try {
      const response = await deleteBook(id);
      document.querySelector('.modal-text').textContent = response.data.message;
      document.querySelector('.modal').classList.toggle('hide-modal');
      // Refresh the book list after deletion
      getAllBooks();
    } catch (error) {
      console.error('Error deleting book:', error);
    }
  };

  // Close the modal
  const closeModal = () => {
    document.querySelector('.modal').classList.toggle('hide-modal');
  };

  // Handle search input change
  const handleSearch = (e) => {
    const query = e.target.value;
    setSearchQuery(query);
    filterBooks(query);
  };

  // Filter books based on search query and sort by number of pages
  const filterBooks = (query) => {
    if (query) {
      const filtered = books.filter((book) =>
        book.title.toLowerCase().includes(query.toLowerCase()) ||
        book.author.toLowerCase().includes(query.toLowerCase()) ||
        (book.genre && book.genre.toLowerCase().includes(query.toLowerCase())) // Filter by genre
      );
      setFilteredBooks(sortBooksByPages(filtered));
    } else {
      setFilteredBooks(books); // Show all books if query is empty
    }
  };

  return (
    <section className='books-page animate__animated animate__backInDown'>
      <h1>Books Collection</h1>

      <div className='search-container'>
        <input
          type='text'
          placeholder='Search by title, author or genre...'
          value={searchQuery}
          onChange={handleSearch}
          aria-label='Search'
        />
      </div>

      <div className='books-container'>
        <div className='modal hide-modal animate__animated animate__tada'>
          <button className='close-btn' onClick={closeModal}>
            X
          </button>
          <p className='modal-text'></p>
          <a href='/books'>
            <button className='nav-back-btn'>Back to Collection</button>
          </a>
        </div>

        {loading && <p>Loading...</p>}

        {filteredBooks.length > 0 ? (
          filteredBooks.map((book) => (
            <div className='book-card' key={book.id}>
              <div className='book-card-header'>
                <h2>
                  Title: <span className='light-text'>{book.title}</span>
                </h2>
                <h3>
                  Author: <span className='light-text'>{book.author}</span>
                </h3>
                <h4>
                  # of Pages: <span className='light-text'>{book.no_of_pages}</span>
                </h4>
                <h4>
                  Publish Date: <span className='light-text'>{book.published_at}</span>
                </h4>
                <h4>
                  Genre: <span className='light-text'>{book.genre}</span>
                </h4>
              </div>
              <div className='book-card-buttons'>
                <a href={`books/editBook/${book.id}`}>
                  <button className='book-card-button edit-btn'>Edit</button>
                </a>
                <button className='book-card-button remove-btn' onClick={() => deleteBookFromCollection(book.id)}>
                  Remove
                </button>
              </div>
            </div>
          ))
        ) : (
          <div className='book-card'>
            <div className='book-card-header'>
              <h2 className='light-text'>No results found</h2>
            </div>
          </div>
        )}
      </div>
    </section>
  );
};

export default Books;
