import React, { useState } from 'react';
import { addBook } from '../../service/api';
import './addBook.css';
import 'animate.css';

const AddBook = () => {
  const [title, setTitle] = useState('');
  const [author, setAuthor] = useState('');
  const [bookPages, setBookPages] = useState('');
  const [genre, setGenre] = useState('');
  const [publishDate, setPublishDate] = useState('');
  const [modalMessage, setModalMessage] = useState('');
  const [modalVisible, setModalVisible] = useState(false);

  console.log(genre,'genre')

  const addNewBook = async () => {
    try {
      const response = await addBook(title, author, bookPages, genre, publishDate);
      setModalMessage(response?.data?.message || 'Book added successfully');
      setModalVisible(true);
    } catch (error) {
      setModalMessage('Error adding book');
      setModalVisible(true);
    }
  };

  const closeModal = () => {
    setModalVisible(false);
  };

  return (
    <section>
      <div className='form-container animate__animated animate__backInDown'>
        {modalVisible && (
          <div className='modal animate__animated animate__tada'>
            <button className='close-btn' onClick={closeModal}>
              X
            </button>
            <p className='modal-text'>{modalMessage}</p>
            <a href='/books'>
              <button className='nav-back-btn'>Back to Collection</button>
            </a>
          </div>
        )}
        <div className='add-book-form'>
          <h1>Add a new book.</h1>
          <div className='input-container'>
            <label>Title: </label>
            <input
              type='text'
              name='title'
              id='title'
              placeholder='Title'
              value={title}
              onChange={(e) => setTitle(e.target.value)}
              required
            />
          </div>
          <div className='input-container'>
            <label>Author: </label>
            <input
              type='text'
              name='author'
              id='author'
              placeholder='Author'
              value={author}
              onChange={(e) => setAuthor(e.target.value)}
              required
            />
          </div>
          <div className='input-container'>
            <label># of Pages: </label>
            <input
              type='number'
              name='no_of_pages'
              id='no_of_pages'
              placeholder='Total Pages'
              value={bookPages}
              onChange={(e) => setBookPages(e.target.value)}
              required
            />
          </div>
          <div className='input-container'>
            <label>Genre: </label>
            <input
              type='text'
              name='genre'
              id='genre'
              placeholder='Genre'
              value={genre}
              onChange={(e) => setGenre(e.target.value)}
              required
            />
          </div>
          <div className='input-container'>
            <label>Date Published: </label>
            <input
              type='date'
              name='publish_date'
              id='publish_date'
              value={publishDate}
              onChange={(e) => setPublishDate(e.target.value)}
              required
            />
          </div>
          <button className='addBook-btn' onClick={addNewBook}>
            Add Book
          </button>
        </div>
      </div>
    </section>
  );
};

export default AddBook;
